var iap = require('./');

var receipt = '';

iap.setup(function (error) {
	if (error) {
		return console.error('setup failed:', error);
	}
	iap.validate(iap.APPLE, receipt, function (error, res) {
		if (error) {
			return console.error('validation failed:', error);
		}

		console.log('validated:', res);

		if (iap.isValidated(res)) {
			console.log('OK');
		}
	});
});
